def fp(t, y): 
	q = y[0:2]
	p = y[2:4]
	q_n = np.linalg.norm(q)
	qp = -(q_n - 1) * q / q_n - np.array([mpf('0'), mpf('1')])
	return np.array([p[0], p[1], qp[0], qp[1]])

